import React, { Component } from 'react'
import { Button, TextField, Typography, FormControl } from "@mui/material"

class ExchangeForm extends Component {
  render() {
    return (
      <div>
        <FormControl variant="outlined" sx={{ width: "30ch" }}>
          <Typography variant="h6">Exchange Deez</Typography>
          <div style={{ minHeight: "3ch" }}></div>
          <TextField
            required
            inputRef={deezAmount => {this.deezAmount = deezAmount}}
            variant="standard"
            placeholder="Deez Amount"
            margin="normal"
            onChange={e => {
              const ethAmount = this.deezAmount.value / 1000
              this.ethAmount.value = ethAmount
            }}
            />
          <TextField
            required
            inputRef={ethAmount => {this.ethAmount = ethAmount}}
            variant="standard"
            placeholder="Eth Amount"
            margin="normal"
            onChange={e => {
              const deezAmount = this.ethAmount.value * 1000
              this.deezAmount.value = deezAmount
            }}
          />
        </FormControl>
        <br />
        <Button sx={{m: 2}} onClick={async (e) => {
          e.preventDefault()
          const ethAmount = this.ethAmount.value
          if (!ethAmount) return
          await this.props.handleBuy(this.props.state.web3.utils.toWei(ethAmount.toString(), "ether"))

          this.deezAmount.value = null
          this.ethAmount.value = null
        }}>
          Buy Deez
        </Button>
        <Button sx={{m: 2}} onClick={async (e) => {
          e.preventDefault()
          const deezAmount = this.deezAmount.value
          if (!deezAmount) return
          await this.props.handleSell(deezAmount)

          this.deezAmount.value = null
          this.ethAmount.value = null
        }}>
          Sell Deez
        </Button>
      </div>
    )
  }
}

export default ExchangeForm
